import React, {Component} from 'react';
import InputFloat from 'react-floating-input';

const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);

class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
          email: null,
          password: null,
          errors: {
            email: '',
            password: '',
          }
        };
      }

      handleChange = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        let errors = this.state.errors;
      
        switch (name) {
          case 'fullName': 
            errors.fullName = 
              value.length < 5
                ? 'Full Name must be 5 characters long!'
                : '';
            break;
          case 'email': 
            errors.email = 
              validEmailRegex.test(value)
                ? ''
                : 'Email is not valid!';
            break;
          case 'password': 
            errors.password = 
              value.length < 8
                ? 'Password must be 8 characters long!'
                : '';
            break;
          default:
            break;
        }
      
        this.setState({errors, [name]: value}, ()=> {
            console.log(errors)
        })
      }


    render() {
      return (
        <div className='wrapper'>
          <div className='form-wrapper' style={{display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', margin:'20%'}}>
            <h2>Register</h2>
                
              <div className='emailaddress'>
              <InputFloat value={this.state.email} placeholder='Email'
            onChange={({ target }) => this.setState({ email: target.value })} style={{width: 200, marginTop: 30}}/>
              </div>
              <div className='password'>
              <label htmlFor="password"></label>
              <InputFloat value={this.state.password}  placeholder='Password'
            onChange={({ target }) => this.setState({ password: target.value })} style={{width: 200, marginTop: 60}}/>
              </div>
              <div className='info'>
                
              </div>
              <div className='submit'>
                <button>
                    
                    Register

                </button>
                </div>

        </div>
      </div>
    );
  }
}

export default Register;
