import React, {Component} from 'react';
import InputFloat from 'react-floating-input';

const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const validateForm = (errors) => {
    let valid = true;
    Object.values(errors).forEach(
      (val) => val.length > 0 && (valid = false)
    );
    return valid;
  }

class Register extends Component {
    constructor(props) {
        super(props);
        this.state = {
          email: null,
          password: null,
          errors: {
            email: '',
            password: '',
          }
        };
      }

      handleChange = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        let errors = this.state.errors;
      
        switch (name) {
          case 'email': 
            errors.email = 
              validEmailRegex.test(value)
                ? ''
                : 'Email is not valid!';
            break;
          case 'password': 
            errors.password = 
              value.length < 8
                ? 'Password must be 8 characters long!'
                : '';
            break;
          default:
            break;
        }
      
        this.setState({errors, [name]: value});

    
      }

      handleSubmit = (event) => {
        event.preventDefault();
        if(validateForm(this.state.errors)) {
          console.info('Valid Form')
        }else{
          console.error('Invalid Form')
        }
      }


    render() {
        const {errors} = this.state;
      return (
        <div className='wrapper'>
          <div className='form-wrapper' style={{display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', margin:'20%'}}>
            <h1>Register</h1>
            <form onSubmit={this.handleSubmit} noValidate> 
              <div className='emailaddress'>
              <InputFloat value={this.state.email} placeholder='Email'
            onChange={({ target }) => this.setState({ email: target.value })} style={{width: 500, marginTop: 30}}/>
              </div>
              <div className='password'>
             
              <InputFloat value={this.state.password}  placeholder='Password' type='password'
            onChange={({ target }) => this.setState({ password: target.value })} style={{width: 500, marginTop: 60}}/>
              </div>
              <div className='info'>
                
              </div>
              <div className='submit'>
                <button>
                    
                    Register

                </button>
                </div>
</form>
        </div>
      </div>
    );
  }
}

export default Register;
